use super::*;
#[derive(Clone, PartialEq)]
pub struct Icon {
    pub handle: HICON,
}
impl Icon {
    pub const unsafe fn null() -> Self {
        Self {
            handle: HICON(NULL_PTR()),
        }
    }
    pub fn is_invalid(&self) -> bool {
        self.handle.0 == NULL_PTR()
    }
    pub fn load_from_module(
        module: ExecutableFile,
        id: Either<&str, u16>,
        width: Option<Size>,
        share: bool,
    ) -> Result<Self> {
        let (pcw, _pcw) = match id {
            Left(l) => str_to_pcwstr(l),
            Right(r) => str_to_pcwstr(&("#".to_owned() + &r.to_string())),
        };
        let Size(cx, cy) = width.unwrap_or(Size(0, 0));
        Ok(Self {
            handle: HICON(
                unsafe {
                    LoadImageW(
                        Some(module.into()),
                        pcw,
                        IMAGE_ICON,
                        cx,
                        cy,
                        if share {
                            IMAGE_FLAGS::default() | LR_SHARED
                        } else {
                            IMAGE_FLAGS::default()
                        },
                    )?
                }
                .0,
            ),
        })
    }
}
impl From<HICON> for Icon {
    fn from(hi: HICON) -> Self {
        Self { handle: hi }
    }
}
impl Into<HICON> for Icon {
    fn into(self) -> HICON {
        self.handle
    }
}
